
public class VisuNumFor {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		for(int cont = 20;cont<50;cont++) { //cuenta desde el 20 hasta el 50 sumando 1 en cada vuelta
			System.out.print(cont+","); //visualiza por consola los numeros hasta 49 con ,
		}
		System.out.println(50); //visualiza el 50

	}

}
